import * as React from 'react';
import Modal from 'nav-frontend-modal';
import KnappBase from 'nav-frontend-knapper';
import { Normaltekst, Undertittel } from 'nav-frontend-typografi';

interface IProps {
    modalIsOpen: boolean;
    onLoggUtClick?: () => void;
    setModalIsOpen: (modalIsOpen: boolean) => void;
}

const LoggUtModal: React.StatelessComponent<IProps> = ({
    modalIsOpen,
    onLoggUtClick,
    setModalIsOpen
}) => {
    return (
        <Modal
            appElement={document.body}
            className={'nav-frontend-internheader__loggutmodal'}
            isOpen={modalIsOpen}
            onRequestClose={() => {
                setModalIsOpen(false);
            }}
            contentLabel={'loggutmodal'}
            closeButton={true}
        >
            <div className={'nav-frontend-internheader__loggutmodal--content'}>
                <Undertittel children={'Logg ut'} />
                <Normaltekst>Er du sikker p√• at du vil logge ut?</Normaltekst>
            </div>
            <div className={'nav-frontend-internheader__loggutmodal--actions'}>
                <KnappBase
                    key={'loggut'}
                    className={
                        'nav-frontend-internheader__loggutmodal--actions-knapp'
                    }
                    type={'hoved'}
                    onClick={onLoggUtClick}
                    children={'Logg ut'}
                />
                <KnappBase
                    key={'avbryt'}
                    className={
                        'nav-frontend-internheader__loggutmodal--actions-knapp'
                    }
                    type={'standard'}
                    onClick={() => setModalIsOpen(false)}
                    children={'Avbryt'}
                />
            </div>
        </Modal>
    );
};

export default LoggUtModal;
