import * as React from 'react';
import * as PT from 'prop-types';
import * as classnames from 'classnames';
import 'nav-frontend-internheader-style';

export interface InternheaderProps {
    /**
     * Egendefinert klassenavn.
     */
    className?: string;

    children: React.ReactChildren;
}

class Internheader extends React.Component<InternheaderProps> {
    render() {
        const { className, children } = this.props;
        return (
            <div className={classnames('nav-frontend-internheader', className)}>
                {children}
            </div>
        );
    }
}

(Internheader as React.ComponentClass).propTypes = {
    /**
     * Egendefinert klassenavn.
     */
    className: PT.string,

    children: PT.node
};

export default Internheader;

