import * as React from 'react';
import { HTMLAttributes } from 'react';
import * as PT from 'prop-types';
import * as classnames from 'classnames';
import {
    Manager,
    Reference,
    Popper,
    PopperProps,
    PopperArrowProps
} from 'react-popper';
import { placements as PopoverOrientering } from 'react-popper/lib/esm/index.js';

import 'nav-frontend-popover-style';

export interface PopoverProps extends HTMLAttributes<HTMLDivElement> {
    /**
     * Rendrer en popover uten pil og uten margin til
     */
    utenPil?: boolean;
    /**
     * Props til pil-element, se [react-popper docs](https://github.com/FezVrasta/react-popper#children).
     */
    pilProps?: PopperArrowProps;
    /**
     * Referanse til intern `<div>`.
     */
    innerRef?: (node?: any) => any;
    /**
     * Bestemmer posisjon av pil i forhold til Popover. StÃ¸tter alle ['placement'-verdiene i PopperJS](https://github.com/FezVrasta/popper.js/blob/master/packages/popper/src/methods/placements.js).
     */
    orientering?: PopoverOrientering;
}

const popoverCls = (props) => classnames('popover', props.className, `popover--${props.orientering}`, {
    'popover--utenPil': props.utenPil
});

class Popover extends React.Component<PopoverProps> {
    static defaultProps: Partial<PopoverProps> = {
        orientering: 'top'
    };
    render() {
        const { children, className, utenPil, pilProps, innerRef, orientering, ...rest } = this.props;
        return (
            <div
                className={popoverCls(this.props)}
                ref={innerRef}
                onClick={(e) => e.stopPropagation()}
                {...rest}
            >
                <div className="popover__inner">{children}</div>
                {
                    !utenPil &&
                    <div className="popover__pil" {...pilProps} />
                }
            </div>
        );
    }
}

(Popover as React.ComponentClass).propTypes = {
    /**
     * Egendefinert klassenavn.
     */
    className: PT.string
};

export default Popover;
export { Manager, Reference, Popper, PopperProps, PopoverOrientering };
